"use client"

import { useState, useMemo, useEffect } from "react"
import { Input } from "@/components/ui/input"
import { Table, TableHeader, TableRow, TableHead, TableBody, TableCell } from "@/components/ui/table"

interface Pool {
  name: string,
  dailyReward: number,
  totalReward: number,
  investmentInUSD: number,
  liquidityAfterDeposit: number,
  position: number,
  positionInPercents: number,
  dailyRewardInUSD: number,
  dailyRewardInPercents: number,
  daysLeft: number,
  maxPossibleRewardInUSD: number,
}

export function Component() {
  const [searchTerms, setSearchTerms] = useState({
    name: "",
    dailyReward: "",
    totalReward: "",
  })
  const [sortColumn, setSortColumn] = useState(null)
  const [sortDirection, setSortDirection] = useState(null)
  const [data, setData] = useState<Pool[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    loadData()
  }, [data, isLoading]);

  const loadData = () => {
    fetch('http://localhost:8091/rewards?deposit=100') // Replace with your API endpoint
      .then(response => response.json())
      .then(json => setData(json))
      .catch(error => console.error('Error fetching data:', error));
    setIsLoading(false)
  }

  const filteredPools = useMemo(() => {
    return data.filter((dat) => {
      return (
        dat.name.toLowerCase().includes(searchTerms.name.toLowerCase()) //&&
        // dat.dailyReward.toLowerCase().includes(searchTerms.dailyReward.toLowerCase()) &&
        // dat.totalReward.toLowerCase().includes(searchTerms.totalReward.toLowerCase())
      )
    })
  }, [searchTerms, data])
  const sortedPools = useMemo(() => {
    if (!sortColumn) return filteredPools
    return filteredPools.sort((a, b) => {
      if (a[sortColumn] < b[sortColumn]) return sortDirection === "asc" ? -1 : 1
      if (a[sortColumn] > b[sortColumn]) return sortDirection === "asc" ? 1 : -1
      return 0
    })
  }, [filteredPools, sortColumn, sortDirection, data])

  const handleSort = (column) => {
    if (sortColumn === column) {
      setSortDirection(sortDirection === "asc" ? "desc" : "asc")
    } else {
      setSortColumn(column)
      setSortDirection("asc")
    }
  }
  const handleSearch = (e) => {
    const { name, value } = e.target
    setSearchTerms((prevState) => ({
      ...prevState,
      [name]: value,
    }))
  }

  if (isLoading && data.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div className="bg-background rounded-lg shadow-lg">
      <div className="p-4 border-b">
        <div className="grid grid-cols-3 gap-4">
          <div>
            <Input
              name="name"
              placeholder="Search by name"
              value={searchTerms.name}
              onChange={handleSearch}
              className="bg-muted"
            />
          </div>
          <div>
            <Input
              name="dailyReward"
              placeholder="Search by dailyReward"
              value={searchTerms.dailyReward}
              onChange={handleSearch}
              className="bg-muted"
            />
          </div>
          <div>
            <Input
              name="totalReward"
              placeholder="Search by totalReward"
              value={searchTerms.totalReward}
              onChange={handleSearch}
              className="bg-muted"
            />
          </div>
        </div>
      </div>
      <Table>
        <TableHeader>
          <TableRow>
            <TableHead className="cursor-pointer" onClick={() => handleSort("name")}>
              Pull Name
              {sortColumn === "name" && <span className="ml-2">{sortDirection === "asc" ? "\u2191" : "\u2193"}</span>}
            </TableHead>
            <TableHead className="cursor-pointer" onClick={() => handleSort("dailyReward")}>
              Daily Reward
              {sortColumn === "dailyReward" && <span className="ml-2">{sortDirection === "asc" ? "\u2191" : "\u2193"}</span>}
            </TableHead>
            <TableHead className="cursor-pointer" onClick={() => handleSort("totalReward")}>
              Total Reward
              {sortColumn === "totalReward" && <span className="ml-2">{sortDirection === "asc" ? "\u2191" : "\u2193"}</span>}
            </TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {sortedPools.map((dat) => (
            <TableRow key={dat.name}>
              <TableCell>{dat.name}</TableCell>
              <TableCell>{dat.dailyReward}</TableCell>
              <TableCell>{dat.totalReward}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  )
}
